//Collection Item Styled Component
import styled from 'styled-components';
import CustomButton from '../custom-button/custom-button.component';

export const CollectionItemcontainer = styled.div`
    width: 22vw;
    display: flex;
    flex-direction: column;
    height: 350px;
    align-items: center;
    position: relative;

    &:hover {
        .image {
          opacity: 0.8;
        }
        button {
          opacity: 0.85;
          display: flex;
        }
    }
`;

export const AddButton = styled(CustomButton)`
    width: 80%;
    opacity: 0.7;
    position: absolute;
    top: 255px;
    display: none;
`;
